var meses = ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio", "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"],
a = new Date,
anho = a.getFullYear();
$(document).ready(function() {
    $("#actual").html(meses[a.getMonth()]), $(".nav-fecha").click(function() {
        var a = parseInt($("#actual").data("mes"));
        $(this).hasClass("anterior") ? 1 == a ? (a = 12, anho -= 1) : a -= 1 : $(this).hasClass("siguiente") && (12 == a ? (a = 1, anho += 1) : a += 1), 
        $("#actual").html(meses[a - 1]), a = a < 10 ? "0" + a : a, 
        $("#actual").data("mes", a), 
        $("#loader").fadeIn(), 
        $.ajax({
            url: contexto + "FiltrarPorMes",
            data: {
                mes: a,
                anho: anho
            },
            success: function(a) {
                $("#loader").fadeOut(), $("#calendario").html(a)
            },
            error: function(a) {
                $("#loader").fadeOut(), $("#calendario").html(a)
            }
        })
    })
});